	write(txEomSize, TxFrameLengthMax);
	write(txEomChar0, TxFrameTerminationChar1);
	write(txEomChar1, TxFrameTerminationChar2);
	write(txEomChar2, TxFrameTerminationChar3);
	write(txEomChar3, TxFrameTerminationChar4);
	write(hshInv, 0x00);
	write(hshTxF, 0x00);
	write(hshClr, 0x00);
	write(hshSet, 0x00);
	write(hshRxF, 0x00);
